import React, { useEffect, useState } from 'react';
import { Input, Space , Button , Table } from 'antd';
import { AudioOutlined } from '@ant-design/icons';
import { useHistory } from 'react-router-dom';

const { Search } = Input;
const onSearch = value => console.log(value);




const Customers = () => {
    let data = [];
    const [heading,setHeading] = useState();
    const [tableData , setTableData] = useState([]);
    let history = useHistory();
    let i =1 ;
    let masterData = [];
    const getData = async (url) => {
        
        let response = await fetch(url,{
            method:'GET',
            headers: {
                "Content-Type": "application/json",
                "Access-Control-Allow-Origin": "*",
                "Accept": "application/json",
            },
            mode: 'cors',
        })
        response.json().then((item) => {
            item.data.forEach(element => {
               masterData.push(element) 
            });
            // setTableData(tableData.concat(item.data));
            if(item.total_pages > i){
                getData(`https://reqres.in/api/users?page=${++i}`);
            }
            else{
                setTableData(masterData);
            }
        })
    }
    useEffect(() => {
        var column = [
            {
                title:'Id',
                dataIndex:'id',
            },
            {
                title:'Email',
                dataIndex:'email',
            },
            {
                title:'First Name',
                dataIndex:'first_name',
            },
            {
                title:'Last Name',
                dataIndex:'last_name',
            },
            {
                title:'Action',
                dataIndex:'',
                render: () => <div><span>Edit</span> <span>Delete</span></div>
            },
        ]
        setHeading(column);
        getData('https://reqres.in/api/users')
    },[])
    return(
        <div>
             <div>My Customers</div>
        <Search placeholder="input search text" onSearch={onSearch} enterButton />
        <Button type="primary" onClick={() => history.push('/add')}>Add user</Button>
        
        <Table
        columns={heading}
       dataSource={tableData}
       bordered={true}
        pagination={false}   
       />
       </div>
    )
}



export default Customers ;
